class ListAppItem extends React.Component {
    render() {
            
        let listItemAdd = this.props.addItems.map(item => <li>{item.name} x {item.quantity}</li>
           )
        return(
            <ul className='listItemAdd'>
            {listItemAdd}
        </ul>
    )
}
}
